import csv
import random
import datetime

# 定义分类和子分类
categories = {
    "餐饮": ["三餐", "买菜", "水果", "零食", "加餐", "下午茶", "烟酒饮品", "粮油调料"],
    "交通": ["公交地铁", "打车", "私家车", "飞机火车", "共享单车"],
    "购物": ["日用品", "衣帽鞋包", "护肤美妆", "饰品", "数码", "电器", "家装"],
    "居家": ["水电煤", "话费", "网费", "房租", "物业", "维修"],
    "人情": ["送礼", "请客", "孝心", "亲密付", "发红包", "借出", "还钱"],
    "医疗": ["药品", "保健", "治疗", "美容"],
    "娱乐": ["休闲", "约会", "聚会", "游戏", "健身"],
    "学习": ["书籍", "培训", "网课"],
    "金融": ["房贷", "车贷", "购物分期", "手续费", "保险", "养卡"],

}

# 定义示例商品名称
products = {
    "餐饮": ["粥", "包子", "油条", "豆浆", "馒头", "炒饭", "饺子", "面条", "盖饭", "牛肉面", "披萨", "汉堡", "寿司", "烤串", "沙拉"],
    "交通": ["公交卡充值", "地铁票", "出租车费", "加油", "机票", "火车票", "共享单车","停车费"],
    "购物": ["卫生纸", "洗发水", "牙膏", "洗衣液", "沐浴露", "衣服", "帽子", "鞋子", "护肤品", "彩妆", "饰品", "手链", "项链", "耳环", "手机", "电脑", "电视", "冰箱", "洗衣机", "沙发"],
    "居家": ["水费", "电费", "煤气费", "电话费", "网络费", "房租", "物业费", "维修费"],
    "人情": ["礼品卡", "鲜花", "人情聚餐", "红包"],
    "医疗": ["感冒药", "止疼药", "维生素", "保健品", "治疗费", "美容费用"],
    "娱乐": ["电影票", "游戏机", "桌游", "乐器", "健身卡"],
    "学习": ["小说", "教科书", "参考书", "杂志", "培训费", "网课"],
    "金融": ["房贷", "车贷","银行手续费", "保险费", "信用卡养卡费"],

}

# 定义商家名称
merchants = {
    "餐饮": ["肯德基", "丽丽国际大酒店", "沙县国际","常德牛肉粉","德仔","一点点"],
    "交通": ["交通公司", "轨交"],
    "购物": ["大润发", "淘宝123店", "拼多多"],
    "居家": ["便捷收费公司A", "便捷收费公司B"],
    "人情": ["人情转账A", "人情转账B"],
    "医疗": ["药店A", "药店B"],
    "娱乐": ["天街", "商场B"],
    "学习": ["微信读书", "书店B"],
    "金融": ["银行A", "银行B"],

}

# 生成200条数据
entries = []

for _ in range(200):
    category = random.choice(list(categories.keys()))
    sub_category = random.choice(categories[category])
    product = random.choice(products[category])
    merchant = random.choice(merchants[category])
    amount = round(random.uniform(5, 1000), 2)  # 生成随机金额
    date = datetime.date(2024, 5, random.randint(1, 31))  # 任选一天作为日期
    entry = [date, category, sub_category, product, amount, merchant]
    entries.append(entry)

# 写入CSV文件
with open('monthly_bill.csv', mode='w', newline='', encoding='utf-8-sig') as file:
    writer = csv.writer(file)
    writer.writerow(["日期", "分类", "子分类", "商品名称", "金额", "商家名称"])
    writer.writerows(entries)

print("CSV 文件生成完毕：monthly_bill.csv")